package Array;

public class Arryas {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int arr[]= {1,2,3,4,5,6,10,54,312,32,4,56,67};
		
		//print in reverse
		System.out.println("Print in reverse order "  );
		for(int i = arr.length-1;i>=0;i--)
		{
			System.out.print(" " + arr[i]);
			
			
		}
		System.out.println();
		System.out.println("====================");
		
		//
	}

}
